import React from "react";
import { useState } from "react";
import Card from "./Card";

const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [isErrorName, setIsErrorName] = useState(false);
    const [isErrorEmail, setIsErrorEmail] = useState(false);
    const [isShowCard, setIsShowCard] = useState(false);
    const [isErrorUser, setIsErrorUser] = useState(false);

    const onChangeName = (e) => setName(e.target.value);
    const onChangeEmail = (e) => setEmail(e.target.value);



    const validateName = (name) => {
        if (name.length >= 5){
            return true;
        }else{
            return false;
        }
    }


    const validateEmail = (email) => {
        if (email.includes("@") & email.includes(".")){
            return true;
        }else{
            return false;
        }
    }

    function onSubmitForm(e){
      e.preventDefault();


      if(validateName(name) && validateEmail(email)){
          setIsErrorUser(false)
          console.log("Paciente guardado")
          setIsShowCard(true)
      }else{
          setIsErrorUser(true)
          console.log("La info es incorrecta")
          setIsShowCard(false)
      }
  }

  function validateUser(){
    if(validateName(name) && validateEmail(email)){
      setIsErrorName(false)
      setIsErrorEmail(false)
      console.log("Paciente guardado")
      setIsShowCard(true)
    }else{
      setIsErrorName(true)
      setIsErrorEmail(true)
      console.log("La info es incorrecta")
      setIsShowCard(false)
  }
  }

  return (
    <>
    <div>
    <h1>Veterinaria DH</h1>
    <h2>Ingresa los datos del paciente</h2>
    <form onSubmit={onSubmitForm}>
      <div>
            <input
                    type="text"
                    placeholder="Ingrese su nombre"
                    value={name}
                    onChange={onChangeName}
                />
            </div>
           
            <div>
                <input
                    type="text"
                    placeholder="Ingrese su email"
                    value={email}
                    onChange={onChangeEmail}
                />

                </div>
                {isErrorUser && <p>Por favor verifique su información nuevamente</p>}
                {!isErrorUser && <p>Gracias {name}, te contactaremos cuanto antes vía mail</p>}
                <button type="submit">Añade al paciente</button>
        </form>
    </div>
    </>
  );
};

export default Form;
